import React, { useState, useEffect } from 'react'
import './Home.css'
import { StyledFirebaseAuth } from 'react-firebaseui'
import LoadingScreen from './LoadingScreen'
import firebase from '../firebase/firebase'
import { FaGoogle, FaFacebook, FaEnvelope } from 'react-icons/fa'

const Home = ({ setUser }) => {
	const [loading, setLoading] = useState(true)
	const [animationComplete, setAnimationComplete] = useState(false)

	const uiConfig = {
		signInflow: 'popup',
		signInOptions: [
			firebase.auth.GoogleAuthProvider.PROVIDER_ID,
			firebase.auth.FacebookAuthProvider.PROVIDER_ID,
			firebase.auth.EmailAuthProvider.PROVIDER_ID,
		],
		callbacks: {
			signInSuccessWithAuthResult: () => false,
		},
	}

	useEffect(() => {
		let isMounted = true

		// Set animation complete after a delay
		const animationTimer = setTimeout(() => {
			if (isMounted) setAnimationComplete(true)
		}, 1000)

		firebase.auth().onAuthStateChanged((user) => {
			if (user && isMounted) {
				setUser({
					uid: firebase.auth().currentUser.uid,
					name: firebase.auth().currentUser.displayName,
					email: firebase.auth().currentUser.email,
				})
				console.log('User Logged In')
			} else {
				console.log('User Signed Out')
				setUser({})
			}
			console.log('auth change')
			if (isMounted) setLoading(false)
		})

		return () => {
			isMounted = false
			clearTimeout(animationTimer)
		}
	}, [setUser])

	return (
		<>
			{loading ? (
				<LoadingScreen />
			) : (
				<div id='Home'>
					<div id='logo'>
						<div id='logo-name'>
							<b>Quiz</b>dom
						</div>
						<div id='description'>
							Create and join quizzes on a single platform. Design engaging trivia quizzes, 
							personality tests, polls, and surveys. Share with your students using a unique code.
						</div>
						<div className="features">
							<div className="feature-item">
								<span className="feature-icon">✓</span> Create unlimited quizzes
							</div>
							<div className="feature-item">
								<span className="feature-icon">✓</span> Real-time results
							</div>
							<div className="feature-item">
								<span className="feature-icon">✓</span> Easy sharing
							</div>
						</div>
					</div>

					<div id='login-card' className={animationComplete ? 'animation-complete' : ''}>
						<label className='login-label'>
							<b>Q</b>
						</label>
						<h2 className="login-title">Sign in to continue</h2>
						<StyledFirebaseAuth
							borderRadius='40px'
							uiConfig={uiConfig}
							firebaseAuth={firebase.auth()}
						/>
						<p className="terms-text">
							By signing in, you agree to our <a href="#">Terms of Service</a> and <a href="#">Privacy Policy</a>
						</p>
					</div>
				</div>
			)}
		</>
	)
}

export default Home
